package spring.project.securitynico.controllers;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import spring.project.securitynico.repositories.UserRepository;import spring.project.securitynico.entities.User;import java.util.List;@RestController@RequestMapping("/api/users")public class ApplicationController {    @Autowired    private UserRepository repository;    @GetMapping    public List<User> users() {        return repository.findAll();    }}