package spring.project.securitynico.entities;import lombok.*;import javax.persistence.*;import java.io.Serializable;@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builder@Entity@Data@Table(name = "roles")public class Role implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    public  Long id;    @Column(unique = true,length = 30)    public String name;    private static final long serialVersionUID = 1L;}