package spring.project.securitynico.entities;import lombok.*;import javax.persistence.*;import java.io.Serializable;import java.util.List;@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builder@Entity@Data@Table(name = "users")public class User implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private Long id;    @Column(name = "username")    private String username;    @Column(name = "password")    private String password;    @Column(name = "active")    private Boolean active;    @Column(name = "name")    public String name;    @Column(name = "email", unique = true, length = 100)    public String email;    @ManyToMany(fetch = FetchType.LAZY)    @JoinTable(name = "user_role",            joinColumns = @JoinColumn(name = "user_id"),            inverseJoinColumns = @JoinColumn(name = "role_id"),            uniqueConstraints = {@UniqueConstraint(columnNames = {"user_id", "role_id"})}    )    public List<Role> roles;}