package spring.project.securitynico.security;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;import spring.project.securitynico.entities.User;import spring.project.securitynico.repositories.UserRepository;@Servicepublic class UserSecurityConfig implements UserDetailsService {    @Autowired    private UserRepository userRepository;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        User user = userRepository.findByUsername(username);        UserPrincipal userPrincipal = new UserPrincipal(user);        return userPrincipal;    }}